package edu.udea.vulnfinder.reportGenerator.file;

import java.io.IOException;
import java.io.InputStream;
import java.util.Scanner;
import org.apache.log4j.Logger;

/**
 * Reads the files and return its String contents
 * @author Camilo Sampedro <camilo.sampedro@udea.edu.co>
 */
public class FilesAsStrings {

    public static final String BASE64LOGO = "data:image/png;base64,";
    private final static Logger LOGGER = Logger.getLogger(FilesAsStrings.class);

    public static String getFileAsString(String file) throws IOException {
        LOGGER.info("Generating " + file + " String");
        InputStream resourceAsStream = FilesAsStrings.class.getResourceAsStream("/" + file);
        Scanner scanner = new Scanner(resourceAsStream, "UTF-8");
        Scanner useDelimiter = scanner.useDelimiter("\\A");
        return useDelimiter.next();
    }
}
